mapper.select:
SELECT  *  FROM  am_employment
where
  is_active = 1

    and  employee_id = #{employeeId}


    and  employment_id = #{employmentId}

SELECT a.out_reason, a.out_date, a.emp_task_id, b.employment_id, b.employee_id, b.company_id, /**用工属性*/ b.employ_property, /**用工属性*/ b.employ_way, b.handle_type, c.doc_num, c.yuliu_doc_num, c.archive_place, /**出库日期*/ c.storage_out_date, c.archive_id, d.id_num,/*证件号 */ d.id_card_type, d.employee_name, b.employ_date, /**退工日期*/ f.resign_date, /**用工反馈*/ c.employ_feedback, f.resign_feedback1, f.resign_feedback2, /**退档日期*/ f.return_doc_date, /**退工送办日期*/ f.resign_handle_date, f.job_centre_feedback_date, e.title
FROM am_employment b LEFT JOIN am_emp_task a on a.emp_task_id = b.emp_task_id LEFT JOIN am_archive c ON b.employment_id = c.employment_id LEFT JOIN am_resign f ON f.employment_id = b.employment_id LEFT JOIN emp_employee d on b.employee_id = d.employee_id LEFT JOIN sal_company e ON e.company_id = b.company_id where 1= 1
and a.task_status = ${taskStatus}
and a.task_category = ${taskCategory}
and ${plid}

SELECT t.task_status, COUNT(t.task_status) AS count FROM ( SELECT a.task_status FROM am_employment b LEFT JOIN am_emp_task a on a.company_id = b.company_id and a.employee_id = b.employee_id LEFT JOIN am_archive c ON b.employment_id = c.employment_id LEFT JOIN am_resign f ON f.employment_id = b.employment_id WHERE task_category = '1'
AND ${ plid }
) t GROUP BY t.task_status

SELECT t.task_status, COUNT(t.task_status) AS count FROM ( SELECT a.task_status FROM am_employment b LEFT JOIN am_emp_task a on a.company_id = b.company_id and a.employee_id = b.employee_id LEFT JOIN am_archive c ON b.employment_id = c.employment_id LEFT JOIN am_resign f ON f.employment_id = b.employment_id WHERE task_category = '2'
AND ${ plid }
) t GROUP BY t.task_status

SELECT a.emp_task_id, b.employment_id, b.employee_id, b.company_id, /**用工属性*/ b.employ_property, /**用工属性*/ b.employ_way, b.handle_type, c.doc_num, c.yuliu_doc_num, c.archive_place, /**出库日期*/ c.storage_out_date, c.archive_id, d.id_num,/*证件号 */ d.id_card_type, b.employ_date, /**退工日期*/ f.resign_date, /**用工反馈*/ c.employ_feedback, f.resign_feedback1, f.resign_feedback2, /**退档日期*/ f.return_doc_date, /**退工送办日期*/ f.resign_handle_date
FROM am_employment b LEFT JOIN am_emp_task a on a.emp_task_id = b.emp_task_id LEFT JOIN am_archive c ON b.employment_id = c.employment_id LEFT JOIN am_resign f ON f.employment_id = b.employment_id LEFT JOIN emp_employee d on b.employee_id = d.employee_id where 1= 1
and a.task_status = ${taskStatus}
and a.task_category = ${taskCategory}
and ${plid}
