mapper.select:
SELECT a.emp_task_id, a.employee_id, a.company_id, f.employment_id, b.employ_property, b.employ_date, b.handle_type, f.resign_date, f.resign_feedback1, f.resign_feedback2, a.out_reason, a.out_date, f.job_centre_feedback_date, d.employee_id, d.employee_name, d.id_num, d.id_card_type, e.title, c.doc_num, c.yuliu_doc_num, c.archive_place, c.storage_out_date, c.employ_feedback, c.luyong_handle_end FROM am_emp_task a LEFT JOIN am_resign f ON f.emp_task_id = a.emp_task_id LEFT JOIN am_employment b ON b.employment_id = f.employment_id LEFT JOIN emp_employee d ON a.employee_id = d.employee_id LEFT JOIN sal_company e ON e.company_id = a.company_id LEFT JOIN am_archive c ON c.employment_id = b.employment_id WHERE a.task_category = '2' and a.employee_id is not null AND a.company_id is not null
and a.task_status = ${taskStatus}
and ${plid}

SELECT t.task_status, COUNT(t.task_status) AS count FROM ( SELECT a.task_status FROM am_emp_task a LEFT JOIN am_resign f ON f.emp_task_id = a.emp_task_id LEFT JOIN am_employment b ON b.employment_id = f.employment_id LEFT JOIN emp_employee d ON a.employee_id = d.employee_id LEFT JOIN sal_company e ON e.company_id = a.company_id LEFT JOIN am_archive c ON c.employment_id = b.employment_id WHERE a.task_category = '2' and a.employee_id is not null AND a.company_id is not null
and ${plid}
) t GROUP BY t.task_status

SELECT a.emp_task_id, a.employee_id, a.company_id, b.employment_id, b.employ_property, b.employ_date, b.handle_type, f.resign_date, f.resign_feedback1, f.resign_feedback2, a.out_reason, a.out_date, f.job_centre_feedback_date, d.employee_id, d.employee_name, d.id_num, d.id_card_type, e.title, c.doc_num, c.yuliu_doc_num, c.archive_place, c.storage_out_date, c.employ_feedback, c.luyong_handle_end FROM am_emp_task a LEFT JOIN am_resign f ON f.emp_task_id = a.emp_task_id LEFT JOIN am_employment b ON b.employment_id = f.employment_id LEFT JOIN emp_employee d ON a.employee_id = d.employee_id LEFT JOIN sal_company e ON e.company_id = a.company_id LEFT JOIN am_archive c ON c.employment_id = b.employment_id WHERE a.task_category = '2' and a.employee_id is not null AND a.company_id is not null
and a.task_status = ${taskStatus}
and ${plid}
