mapper.select:
SELECT  *  FROM  am_employment
where
company_id = #{companyId}
and
employee_id = #{employeeId}
order by created_time desc

SELECT a.employ_code, w.out_reason, w.out_date, w.emp_task_id as empTaskResignId, a.emp_task_id, a.change_company, a.job, b.employment_id, a.employee_id, a.company_id, /**用工属性*/ b.employ_property, /**用工属性*/ b.employ_way, b.handle_type, c.doc_type, c.doc_num, c.yuliu_doc_type, c.yuliu_doc_num, c.archive_place, /**出库日期*/ c.storage_out_date, c.archive_id, d.id_num,/*证件号 */ d.id_card_type, d.employee_name, d.gender, b.employ_date, /**退工日期*/ f.resign_date, /**用工反馈*/ c.employ_feedback, f.resign_feedback, /**退档日期*/ f.return_doc_date, /**退工送办日期*/ f.resign_handle_date, f.job_centre_feedback_date, f.end_type, h.title, h.service_center,/*服务中心 */ h.leader_ship_name, /*客服经理*/ cs.employ_special, cs.refuse_special, cs.archive_special FROM am_emp_task a LEFT JOIN am_employment b on a.emp_task_id = b.emp_task_id LEFT JOIN am_archive c ON b.employment_id = c.employment_id LEFT JOIN am_resign f ON f.employment_id = b.employment_id LEFT JOIN emp_employee d on a.employee_id = d.employee_id LEFT JOIN am_emp_task w on f.emp_task_id = w.emp_task_id LEFT JOIN am_emp_custom h on h.emp_task_id = a.emp_task_id LEFT JOIN am_company_set cs on cs.company_id = a.company_id where 1= 1
and c.luyong_handle_end = ${taskStatus}
and a.job = #{job}
and ${plid}
order by
${plid},
a.created_time desc

SELECT a.employ_code, w.out_reason, w.out_date, w.emp_task_id as empTaskResignId, a.emp_task_id, a.change_company, a.job, b.employment_id, b.employee_id, b.company_id, /**用工属性*/ b.employ_property, /**用工属性*/ b.employ_way, b.handle_type, c.doc_type, c.doc_num, c.yuliu_doc_type, c.yuliu_doc_num, c.archive_place, /**出库日期*/ c.storage_out_date, c.archive_id, d.id_num,/*证件号 */ d.id_card_type, d.employee_name, b.employ_date, /**退工日期*/ f.resign_date, /**用工反馈*/ c.employ_feedback, f.resign_feedback, /**退档日期*/ f.return_doc_date, /**退工送办日期*/ f.resign_handle_date, f.job_centre_feedback_date, h.title, h.service_center,/*服务中心 */ h.leader_ship_name, /*客服经理*/ cs.employ_special, cs.refuse_special, cs.archive_special FROM am_employment b LEFT JOIN am_emp_task a on a.emp_task_id = b.emp_task_id LEFT JOIN am_archive c ON b.employment_id = c.employment_id LEFT JOIN am_resign f ON f.employment_id = b.employment_id LEFT JOIN emp_employee d on b.employee_id = d.employee_id LEFT JOIN am_emp_task w on f.emp_task_id = w.emp_task_id LEFT JOIN am_emp_custom h on h.emp_task_id = a.emp_task_id LEFT JOIN am_company_set cs on cs.company_id = a.company_id where 1= 1
and w.task_status = ${taskResignStatus}
and w.task_status not in ('99','98','1','2','3','4','5')
and w.task_category = ${taskCategory}
and ${plid}
order by b.created_time desc

SELECT t.task_status, COUNT(t.task_status) AS count FROM ( SELECT a.task_status FROM am_employment b LEFT JOIN am_emp_task a on a.emp_task_id = b.emp_task_id LEFT JOIN am_archive c ON b.employment_id = c.employment_id LEFT JOIN am_resign f ON f.employment_id = b.employment_id LEFT JOIN emp_employee d on b.employee_id = d.employee_id LEFT JOIN am_emp_task w on f.emp_task_id = w.emp_task_id LEFT JOIN am_emp_custom h on h.emp_task_id = a.emp_task_id WHERE a.task_category = 1
AND ${ plid }
) t GROUP BY t.task_status

SELECT t.task_status, COUNT(t.task_status) AS count FROM ( SELECT w.task_status FROM am_employment b LEFT JOIN am_emp_task a on a.emp_task_id = b.emp_task_id LEFT JOIN am_archive c ON b.employment_id = c.employment_id LEFT JOIN am_resign f ON f.employment_id = b.employment_id LEFT JOIN emp_employee d on b.employee_id = d.employee_id LEFT JOIN am_emp_task w on f.emp_task_id = w.emp_task_id LEFT JOIN am_emp_custom h on h.emp_task_id = a.emp_task_id WHERE w.task_category = 2
AND ${ plid }
) t GROUP BY t.task_status

SELECT a.employ_code, w.out_reason, w.out_date, w.emp_task_id as empTaskResignId, a.emp_task_id, a.change_company, b.employment_id, b.employee_id, b.company_id, /**用工属性*/ b.employ_property, /**用工属性*/ b.employ_way, b.handle_type, c.doc_num, c.yuliu_doc_num, c.archive_place, /**出库日期*/ c.storage_out_date, c.archive_id, d.id_num,/*证件号 */ d.id_card_type, d.employee_name, b.employ_date, /**退工日期*/ f.resign_date, /**用工反馈*/ c.employ_feedback, f.resign_feedback, /**退档日期*/ f.return_doc_date, /**退工送办日期*/ f.resign_handle_date, f.job_centre_feedback_date, h.title, h.service_center,/*服务中心 */ h.leader_ship_name, /*客服经理*/ cs.archive_special FROM am_employment b LEFT JOIN am_emp_task a on a.emp_task_id = b.emp_task_id LEFT JOIN am_archive c ON b.employment_id = c.employment_id LEFT JOIN am_resign f ON f.employment_id = b.employment_id LEFT JOIN emp_employee d on b.employee_id = d.employee_id LEFT JOIN am_emp_task w on f.emp_task_id = w.emp_task_id LEFT JOIN am_emp_custom h on h.emp_task_id = a.emp_task_id LEFT JOIN am_company_set cs on cs.company_id = a.company_id where 1= 1
and a.task_status = ${taskStatus}
and a.task_category = ${taskCategory}
and ${plid}
