mapper.select:
SELECT  *  FROM  am_employment
where
company_id = #{companyId}
and
employee_id = #{employeeId}
order by created_time desc

SELECT DISTINCT a.employ_code, w.out_reason, w.out_date, w.emp_task_id as empTaskResignId, a.emp_task_id, a.change_company, a.job, b.employment_id, a.employee_id, a.company_id, b.employ_property, b.employ_way, b.handle_type, b.open_af_date, b.employ_style, c.doc_type, c.doc_num, c.yuliu_doc_type, c.yuliu_doc_num, c.archive_place, c.storage_out_date, c.archive_id, c.luyong_handle_end, d.id_num,/*证件号 */ d.id_card_type, d.employee_name, d.gender, b.employ_date, f.resign_date, c.employ_feedback, c.employ_feedback_opt_date, c.diaodang_feedback_opt_date, c.doc_halfway_out_date, f.resign_feedback, f.return_doc_date, f.resign_handle_date, f.job_centre_feedback_date, f.end_type, f.if_labor_manual_return, sal.title, h.service_center, h.leader_ship_name, cs.employ_special, cs.refuse_special, cs.archive_special, e.labor_start_date, e.labor_end_date, e.position, e.company_type FROM am_emp_task a LEFT JOIN am_employment b on a.emp_task_id = b.emp_task_id LEFT JOIN am_archive c ON b.employment_id = c.employment_id LEFT JOIN am_resign f ON f.employment_id = b.employment_id LEFT JOIN emp_employee d on a.employee_id = d.employee_id LEFT JOIN am_emp_task w on f.emp_task_id = w.emp_task_id LEFT JOIN sal_company sal ON a.company_id = sal.company_id LEFT JOIN am_emp_custom h on h.emp_task_id = a.emp_task_id LEFT JOIN am_company_set cs on cs.company_id = a.company_id LEFT JOIN am_emp_employee e ON e.emp_task_id = a.emp_task_id where a.task_category = '1'
and a.job = #{job}
and a.task_status = ${taskStatus}
and a.task_status not in ('1','3','4','5','11')
and a.task_status = ${taskResignStatus}
and a.task_status not in ('99','98','1','2','3','4','5')
and ${plid}
order by
${plid},
a.created_time desc

SELECT a.employ_code, w.out_reason, w.out_date, w.emp_task_id as empTaskResignId, a.emp_task_id, a.change_company, a.job, b.employment_id, b.employee_id, b.company_id, /**用工属性*/ b.employ_property, /**用工属性*/ b.employ_way, b.handle_type, c.doc_type, c.doc_num, c.yuliu_doc_type, c.yuliu_doc_num, c.archive_place, /**出库日期*/ c.storage_out_date, c.archive_id, d.id_num,/*证件号 */ d.id_card_type, d.employee_name, b.employ_date, /**退工日期*/ f.resign_date, /**用工反馈*/ c.employ_feedback, f.resign_feedback, /**退档日期*/ f.return_doc_date, /**退工送办日期*/ f.resign_handle_date, f.job_centre_feedback_date, h.title, h.service_center,/*服务中心 */ h.leader_ship_name, /*客服经理*/ cs.employ_special, cs.refuse_special, cs.archive_special FROM am_employment b LEFT JOIN am_emp_task a on a.emp_task_id = b.emp_task_id LEFT JOIN am_archive c ON b.employment_id = c.employment_id LEFT JOIN am_resign f ON f.employment_id = b.employment_id LEFT JOIN emp_employee d on b.employee_id = d.employee_id LEFT JOIN am_emp_task w on f.emp_task_id = w.emp_task_id LEFT JOIN am_emp_custom h on h.emp_task_id = a.emp_task_id LEFT JOIN am_company_set cs on cs.company_id = a.company_id where 1= 1
and w.task_status = ${taskResignStatus}
and w.task_status not in ('99','98','1','2','3','4','5')
and w.task_category = ${taskCategory}
and ${plid}
order by b.created_time desc

SELECT t.task_status, COUNT(t.task_status) AS count FROM ( SELECT a.task_status FROM am_employment b LEFT JOIN am_emp_task a on a.emp_task_id = b.emp_task_id LEFT JOIN am_archive c ON b.employment_id = c.employment_id LEFT JOIN am_resign f ON f.employment_id = b.employment_id LEFT JOIN emp_employee d on b.employee_id = d.employee_id LEFT JOIN am_emp_task w on f.emp_task_id = w.emp_task_id LEFT JOIN am_emp_custom h on h.emp_task_id = a.emp_task_id WHERE a.task_category = '1'
AND ${ plid }
) t GROUP BY t.task_status

SELECT t.task_status, COUNT(t.task_status) AS count FROM ( SELECT w.task_status FROM am_employment b LEFT JOIN am_emp_task a on a.emp_task_id = b.emp_task_id LEFT JOIN am_archive c ON b.employment_id = c.employment_id LEFT JOIN am_resign f ON f.employment_id = b.employment_id LEFT JOIN emp_employee d on b.employee_id = d.employee_id LEFT JOIN am_emp_task w on f.emp_task_id = w.emp_task_id LEFT JOIN am_emp_custom h on h.emp_task_id = a.emp_task_id WHERE w.task_category = 2
AND ${ plid }
) t GROUP BY t.task_status

SELECT a.employ_code, w.out_reason, w.out_date, w.emp_task_id as empTaskResignId, a.emp_task_id, a.change_company, b.employment_id, b.employee_id, b.company_id, /**用工属性*/ b.employ_property, /**用工属性*/ b.employ_way, b.handle_type, c.doc_num, c.yuliu_doc_num, c.archive_place, /**出库日期*/ c.storage_out_date, c.archive_id, d.id_num,/*证件号 */ d.id_card_type, d.employee_name, b.employ_date, /**退工日期*/ f.resign_date, /**用工反馈*/ c.employ_feedback, f.resign_feedback, /**退档日期*/ f.return_doc_date, /**退工送办日期*/ f.resign_handle_date, f.job_centre_feedback_date, h.title, h.service_center,/*服务中心 */ h.leader_ship_name, /*客服经理*/ cs.archive_special FROM am_employment b LEFT JOIN am_emp_task a on a.emp_task_id = b.emp_task_id LEFT JOIN am_archive c ON b.employment_id = c.employment_id LEFT JOIN am_resign f ON f.employment_id = b.employment_id LEFT JOIN emp_employee d on b.employee_id = d.employee_id LEFT JOIN am_emp_task w on f.emp_task_id = w.emp_task_id LEFT JOIN am_emp_custom h on h.emp_task_id = a.emp_task_id LEFT JOIN am_company_set cs on cs.company_id = a.company_id where 1= 1
and a.task_status = ${taskStatus}
and a.task_category = ${taskCategory}
and ${plid}

SELECT t.luyong_handle_end,COUNT(*) as count from ( SELECT a.employ_code, w.out_reason, w.out_date, w.emp_task_id as empTaskResignId, a.emp_task_id, a.change_company, a.job, b.employment_id, a.employee_id, a.company_id, /**用工属性*/ b.employ_property, /**用工属性*/ b.employ_way, b.handle_type, c.doc_type, c.doc_num, c.yuliu_doc_type, c.yuliu_doc_num, c.archive_place, /**出库日期*/ c.storage_out_date, c.archive_id, c.luyong_handle_end, d.id_num,/*证件号 */ d.id_card_type, d.employee_name, d.gender, b.employ_date, /**退工日期*/ f.resign_date, /**用工反馈*/ c.employ_feedback, f.resign_feedback, /**退档日期*/ f.return_doc_date, /**退工送办日期*/ f.resign_handle_date, f.job_centre_feedback_date, f.end_type, sal.title, h.service_center,/*服务中心 */ h.leader_ship_name, /*客服经理*/ cs.employ_special, cs.refuse_special, cs.archive_special FROM am_emp_task a LEFT JOIN am_employment b on a.emp_task_id = b.emp_task_id LEFT JOIN am_archive c ON b.employment_id = c.employment_id LEFT JOIN am_resign f ON f.employment_id = b.employment_id LEFT JOIN emp_employee d on a.employee_id = d.employee_id LEFT JOIN sal_company sal ON a.company_id = sal.company_id LEFT JOIN am_emp_task w on f.emp_task_id = w.emp_task_id LEFT JOIN am_emp_custom h on h.emp_task_id = a.emp_task_id LEFT JOIN am_company_set cs on cs.company_id = a.company_id where a.task_category = '1'
and a.job = #{job}
and ${plid}
) t GROUP BY t.luyong_handle_end

SELECT t.job, COUNT(*) as count from ( SELECT DISTINCT a.employ_code, w.out_reason, w.out_date, w.emp_task_id as empTaskResignId, a.emp_task_id, a.change_company, a.job, w.job as r_job, b.employment_id, a.employee_id, a.company_id, b.employ_property, b.employ_way, b.handle_type, b.open_af_date, b.employ_style, c.doc_type, c.doc_num, c.yuliu_doc_type, c.yuliu_doc_num, c.archive_place, c.storage_out_date, c.archive_id, c.luyong_handle_end, d.id_num,/*证件号 */ d.id_card_type, d.employee_name, d.gender, b.employ_date, f.resign_date, c.employ_feedback, c.employ_feedback_opt_date, c.diaodang_feedback_opt_date, c.doc_halfway_out_date, f.resign_feedback, f.return_doc_date, f.resign_handle_date, f.job_centre_feedback_date, f.end_type, f.if_labor_manual_return, sal.title, h.service_center, h.leader_ship_name, cs.employ_special, cs.refuse_special, cs.archive_special, e.labor_start_date, e.labor_end_date, e.position, e.company_type FROM am_emp_task a LEFT JOIN am_employment b on a.emp_task_id = b.emp_task_id LEFT JOIN am_archive c ON b.employment_id = c.employment_id LEFT JOIN am_resign f ON f.employment_id = b.employment_id LEFT JOIN emp_employee d on a.employee_id = d.employee_id LEFT JOIN am_emp_task w on f.emp_task_id = w.emp_task_id LEFT JOIN sal_company sal ON a.company_id = sal.company_id LEFT JOIN am_emp_custom h on h.emp_task_id = a.emp_task_id LEFT JOIN am_company_set cs on cs.company_id = a.company_id LEFT JOIN am_emp_employee e ON e.emp_task_id = a.emp_task_id where a.task_category = '1'
