mapper.select:
SELECT DISTINCT a.emp_task_id, a.employee_id, a.company_id, a.change_company, a.employ_code, f.employment_id, a.employ_property, b.employ_date, b.handle_type, f.resign_date, f.resign_feedback, a.out_reason, a.out_date, a.job, f.job_centre_feedback_date, d.employee_name, d.id_num, d.id_card_type, c.doc_num, c.yuliu_doc_num, c.archive_place, c.storage_out_date, c.employ_feedback, c.luyong_handle_end, sal.title, h.service_center, h.leader_ship_name, cs.refuse_special FROM am_emp_task a LEFT JOIN am_resign f ON f.emp_task_id = a.emp_task_id LEFT JOIN am_employment b ON b.employment_id = f.employment_id LEFT JOIN emp_employee d ON a.employee_id = d.employee_id LEFT JOIN am_archive c ON c.employment_id = b.employment_id LEFT JOIN sal_company sal ON a.company_id = sal.company_id LEFT JOIN am_emp_custom h on h.emp_task_id = b.emp_task_id LEFT JOIN am_emp_employee e on e.emp_task_id = b.emp_task_id LEFT JOIN am_company_set cs on cs.company_id = a.company_id WHERE a.task_category = '2' and a.employee_id is not null and a.company_id is not null
and a.task_status = ${taskStatus}
and a.task_status not in ('99','98','1','2','3','4','5')
and a.job = #{job}
and ${plid}
order by
${plid},
a.created_time desc

SELECT DISTINCT a.emp_task_id, a.employee_id, a.company_id, a.employ_code, a.change_company, f.employment_id, b.employ_property, b.employ_date, b.handle_type, f.resign_date, f.resign_feedback, a.out_reason, a.out_date, f.job_centre_feedback_date, d.employee_name, d.id_num, d.id_card_type, c.doc_num, c.yuliu_doc_num, c.archive_place, c.storage_out_date, c.employ_feedback, h.title, h.service_center, h.leader_ship_name, cs.refuse_special FROM am_emp_task a LEFT JOIN am_resign f ON f.emp_task_id = a.emp_task_id LEFT JOIN am_employment b ON b.employment_id = f.employment_id LEFT JOIN emp_employee d ON a.employee_id = d.employee_id LEFT JOIN am_archive c ON c.employment_id = b.employment_id LEFT JOIN am_emp_custom h on h.emp_task_id = b.emp_task_id LEFT JOIN am_company_set cs on cs.company_id = a.company_id WHERE a.task_category = '2' and a.employee_id is not null and a.company_id is not null
and a.task_status not in ('99','98','1','2','3','4','5')
and a.job = #{job}
and ${plid}
order by a.created_time desc

SELECT t.task_status, COUNT(t.task_status) AS count FROM ( SELECT DISTINCT a.emp_task_id, a.employee_id, a.company_id, a.change_company, a.employ_code, a.task_status, f.employment_id, b.employ_property, b.employ_date, b.handle_type, f.resign_date, f.resign_feedback, a.out_reason, a.out_date, a.job, f.job_centre_feedback_date, d.employee_name, d.id_num, d.id_card_type, c.doc_num, c.yuliu_doc_num, c.archive_place, c.storage_out_date, c.employ_feedback, c.luyong_handle_end, sal.title, h.service_center, h.leader_ship_name, cs.refuse_special FROM am_emp_task a LEFT JOIN am_resign f ON f.emp_task_id = a.emp_task_id LEFT JOIN am_employment b ON b.employment_id = f.employment_id LEFT JOIN emp_employee d ON a.employee_id = d.employee_id LEFT JOIN am_archive c ON c.employment_id = b.employment_id LEFT JOIN sal_company sal ON a.company_id = sal.company_id LEFT JOIN am_emp_custom h on h.emp_task_id = b.emp_task_id LEFT JOIN am_emp_employee e on e.emp_task_id = b.emp_task_id LEFT JOIN am_company_set cs on cs.company_id = a.company_id WHERE a.task_category = '2' and a.employee_id is not null and a.company_id is not null
and a.job = #{job}
and ${plid}
) t GROUP BY t.task_status

