mapper.select:
select

from fb_market_activity


        AND activity_title = #{activityTitle,jdbcType=VARCHAR}


        AND publisher = #{publisher,jdbcType=VARCHAR}


        AND begin_time = #{beginTime,jdbcType=TIMESTAMP}


        AND end_time = #{endTime,jdbcType=TIMESTAMP}


        AND status = #{status,jdbcType=TINYINT}


        AND content = #{content,jdbcType=VARCHAR}


        AND gift_form = #{giftForm,jdbcType=VARCHAR}


        AND send_way = #{sendWay,jdbcType=TINYINT}


        AND is_active = #{isActive,jdbcType=BIT}


        AND create_time = #{createTime,jdbcType=TIMESTAMP}


        AND modified_time = #{modifiedTime,jdbcType=TIMESTAMP}


        AND created_by = #{createdBy,jdbcType=VARCHAR}


        AND modified_by = #{modifiedBy,jdbcType=VARCHAR}
