mapper.select:
SELECT ea.emp_archive_id,eab.emp_archive_id_bc,com.company_id,com.title,ea.belong_vendor, af.custom_group, afemp.status AS 'emp_status', ca.hf_account_type,emp.employee_id,emp.employee_name,emp.id_num,ea.hf_emp_account,ea.archive_task_status ,eab.hf_emp_account_bc,eab.archive_task_status_bc,ea.operation_remind,ea.operation_remind_date FROM hf_emp_archive ea LEFT JOIN sal_company com ON com.company_id=ea.company_id INNER JOIN emp_employee emp ON emp.employee_id=ea.employee_id INNER JOIN hf_com_account ca ON ca.com_account_id=ea.com_account_id LEFT JOIN (SELECT hea.emp_archive_id as 'emp_archive_id_bc',hea.belong_emp_archive_id,hea.hf_emp_account as 'hf_emp_account_bc',hea.archive_task_status as 'archive_task_status_bc' FROM hf_emp_archive hea WHERE hea.is_active=1 AND hea.hf_type=2)eab ON eab.belong_emp_archive_id=ea.emp_archive_id LEFT JOIN sal_af_company af on ea.company_id=af.company_id LEFT JOIN emp_af_emp_company afemp on afemp.employee_id=ea.employee_id AND afemp.company_id=ea.company_id AND af.status=2 WHERE ea.is_active=1 AND ea.hf_type=1
AND ea.employee_id=#{employeeId}
AND emp.employee_name LIKE CONCAT('%',#{employeeName}, '%')
AND ca.hf_account_type=#{hfAccountType}
AND ca.payment_bank=#{paymentBank}
AND emp.id_num LIKE CONCAT('%',#{idNum}, '%')
AND ea.operation_remind=#{operationRemind}
AND afemp.status=#{empStatus}
--AND af.custom_group=''
AND EXISTS (select 1 from hf_com_account_class cac WHERE cac.com_account_class_id=ea.com_account_class_id AND cac.hf_com_account=${hfComAccount})

SELECT ea.hf_emp_account,ea.archive_task_status,abp.base_amount,abp.start_month, abp.end_month,abp.ratio_emp+ratio_com as 'ratio',abp.amount FROM hf_archive_base_period abp INNER JOIN hf_emp_archive ea ON ea.emp_archive_id=abp.emp_archive_id WHERE abp.is_active=1 AND ea.emp_archive_id in(select emp_archive_id from hf_emp_archive where emp_archive_id=#{empArchiveId} OR belong_emp_archive_id=#{empArchiveId} )
AND ea.hf_type=#{hfType}
ORDER BY abp.archive_base_period_id DESC LIMIT 1

SELECT ea.emp_archive_id,ea.hf_emp_account FROM
emp_employee emp INNER JOIN hf_emp_archive ea on ea.employee_id=emp.employee_id
WHERE ea.is_active=1 and emp.employee_name = #{empName} and emp.id_num= #{idNum};

SELECT het.emp_task_id, hea.emp_archive_id FROM hf_emp_archive hea INNER JOIN hf_emp_task het ON hea.company_id = het.company_id AND hea.employee_id = het.employee_id
hea.is_active = 1 AND het.is_active = 1
#{item}
