SELECT pa.payment_account_id,pa.com_account_id,acr.company_id,pa.com_account_class_id,ca.payment_bank,pa.hf_type,ca.hf_account_type ,(SELECT SUM(ifnull(amount, 0)) FROM hf_month_charge mc INNER JOIN hf_emp_archive ea ON ea.emp_archive_id = mc.emp_archive_id WHERE mc.is_active = 1 AND ea.is_active = 1 AND mc.hf_month = pa.payment_month AND mc.hf_type = pa.hf_type AND ea.com_account_class_id = pa.com_account_class_id AND ea.company_id=acr.company_id AND mc.payment_type = 1 ) AS amount_standard, (SELECT SUM(ifnull(amount, 0)) FROM hf_month_charge mc INNER JOIN hf_emp_archive ea ON ea.emp_archive_id = mc.emp_archive_id WHERE mc.is_active = 1 AND ea.is_active = 1 AND mc.hf_month = pa.payment_month AND mc.hf_type = pa.hf_type AND ea.com_account_class_id = pa.com_account_class_id AND ea.company_id=acr.company_id AND mc.payment_type IN (2, 3, 4, 5) ) AS remitted_amount_add, (SELECT SUM(ifnull(amount, 0)) FROM hf_month_charge mc INNER JOIN hf_emp_archive ea ON ea.emp_archive_id = mc.emp_archive_id WHERE mc.is_active = 1 AND ea.is_active = 1 AND mc.hf_month = pa.payment_month AND mc.hf_type = pa.hf_type AND ea.com_account_class_id = pa.com_account_class_id AND ea.company_id=acr.company_id AND mc.payment_type IN (7, 8, 9, 10) ) AS remitted_amount_reduce, (SELECT ifnull(amount_standard,0) + ifnull(remitted_amount_add,0) - ifnull(remitted_amount_reduce,0)) AS remitted_amount, (SELECT SUM(ifnull(amount, 0)) FROM hf_month_charge mc INNER JOIN hf_emp_archive ea ON ea.emp_archive_id = mc.emp_archive_id WHERE mc.is_active = 1 AND ea.is_active = 1 AND mc.hf_month = pa.payment_month AND mc.hf_type = pa.hf_type AND ea.com_account_class_id = pa.com_account_class_id AND ea.company_id=acr.company_id AND mc.payment_type IN (6, 11) ) AS repair_amount, (SELECT count(DISTINCT mc.employee_id) FROM hf_month_charge mc INNER JOIN hf_emp_archive ea ON ea.emp_archive_id = mc.emp_archive_id WHERE mc.is_active = 1 AND ea.is_active = 1 AND mc.hf_month = pa.payment_month AND mc.hf_type = pa.hf_type AND ea.com_account_class_id = pa.com_account_class_id AND ea.company_id=acr.company_id AND mc.payment_type IN (6, 11) ) AS repair_count_emp, (SELECT count(DISTINCT mc.employee_id) FROM hf_month_charge mc INNER JOIN hf_emp_archive ea ON ea.emp_archive_id = mc.emp_archive_id WHERE mc.is_active = 1 AND ea.is_active = 1 AND mc.hf_month = pa.payment_month AND mc.hf_type = pa.hf_type AND ea.com_account_class_id = pa.com_account_class_id AND ea.company_id=acr.company_id AND mc.payment_type = 1 ) AS remitted_emp_count_standard, (SELECT count(DISTINCT mc.employee_id) FROM hf_month_charge mc INNER JOIN hf_emp_archive ea ON ea.emp_archive_id = mc.emp_archive_id WHERE mc.is_active = 1 AND ea.is_active = 1 AND mc.hf_month = pa.payment_month AND mc.hf_type = pa.hf_type AND ea.com_account_class_id = pa.com_account_class_id AND ea.company_id=acr.company_id AND mc.payment_type IN ( 2, 3, 4, 5) ) AS remitted_emp_count_add, (SELECT count(DISTINCT mc.employee_id) FROM hf_month_charge mc INNER JOIN hf_emp_archive ea ON ea.emp_archive_id = mc.emp_archive_id WHERE mc.is_active = 1 AND ea.is_active = 1 AND mc.hf_month = pa.payment_month AND mc.hf_type = pa.hf_type AND ea.com_account_class_id = pa.com_account_class_id AND ea.company_id=acr.company_id AND mc.payment_type IN (7, 8, 9, 10) ) AS remitted_count_emp_reduce, (SELECT remitted_emp_count_standard + remitted_emp_count_add - remitted_count_emp_reduce) AS remitted_count_emp
FROM hf_payment_account pa INNER JOIN hf_account_com_relation acr ON acr.com_account_id=pa.com_account_id INNER JOIN hf_com_account ca ON ca.com_account_id=pa.com_account_id WHERE pa.is_active=1 AND pa.payment_status=3 AND pa.com_account_id in (SELECT com_account_id FROM hf_payment_account WHERE payment_month= #{paymentMonth} AND ifnull(payment_id,0)=0 AND payment_account_id in
#{item}
)
