mapper.select:
SELECT hft.com_task_id, hca.com_account_id, hcac.com_account_class_id, hcac.hf_com_account, hcac.com_start_month, hcac.operate_start_month, hft.task_category, ctc.task_category_name, ht.hf_type_name, hft.hf_type, hft.company_id, cc.title as company_name, hft.handle_user_id, hft.handle_user_name, hft.submitter_id, hft.submitter_name, DATE_FORMAT(hft.submit_time, '%Y-%m-%d %H:%i') AS submit_time_string, hft.submit_remark, hft.com_account_name, hft.close_day, hft.payment_way, ctpw.com_task_payment_way_name, hft.task_status, hcas.com_account_state_value, hcapb.payment_bank_value, hft.end_month, hca.ukey_store, hcau.ukstore_value, hcat.type_value, hctet.end_type_value, DATE_FORMAT(hft.strart_handle_date, '%Y-%m-%d %H:%i') AS strart_handle_date_string, DATE_FORMAT(hft.send_check_date, '%Y-%m-%d %H:%i') AS send_check_date_string, DATE_FORMAT(hft.finish_date, '%Y-%m-%d %H:%i') AS finish_date_string, hca.remark as comAccountRemark FROM hf_com_task hft LEFT JOIN hf_com_account hca ON hft.com_account_id = hca.com_account_id LEFT JOIN hf_com_account_class hcac ON hft.com_account_class_id = hcac.com_account_class_id LEFT JOIN hf_com_account_state hcas ON hca.state = hcas.com_account_state_code LEFT JOIN hf_com_account_payment_bank hcapb ON hca.payment_bank = hcapb.payment_bank_code LEFT JOIN hf_com_account_ukstore hcau ON hca.ukey_store = hcau.ukstore_code LEFT JOIN hf_com_account_type hcat ON hca.hf_account_type = hcat.type_code
LEFT JOIN sal_company cc ON hft.company_id = cc.company_id LEFT JOIN hf_com_task_category ctc ON hft.task_category = ctc.task_category_code LEFT JOIN hf_type ht ON hft.hf_type = ht.hf_type_code LEFT JOIN hf_com_task_payment_way ctpw ON hft.payment_way = ctpw.com_task_payment_way_code LEFT JOIN hf_com_task_end_type hctet ON hft.end_type = hctet.end_type_code
AND hft.is_active = 1
AND cc.title LIKE CONCAT('%', #{companyName}, '%')
AND hft.task_category = #{taskCategory}
AND hft.company_id = #{companyId}
AND ht.hf_type_name = TRIM(#{hfTypeName})
AND hft.hf_type = #{hfType}
AND hft.task_status IN (${taskStatusString})
AND hft.task_status =${taskStatus}
AND hca.hf_account_type = ${hfAccountType}
AND hca.payment_bank = ${paymentBank}
ORDER BY hft.com_task_id

SELECT hft.com_task_id, hca.com_account_id, hcac.com_account_class_id, hcac.hf_com_account, hcac.com_start_month, hcac.operate_start_month, hft.task_category, ctc.task_category_name, ht.hf_type_name, hft.hf_type, hft.company_id, hft.task_status, cc.title as company_name, hft.handle_user_id, hft.handle_user_name, hft.submitter_id, hft.submitter_name, DATE_FORMAT(hft.submit_time, '%Y-%m-%d %H:%i') AS submit_time_string, hft.submit_remark, hca.com_account_name, hca.close_day, hca.payment_way, hca.payment_bank, hcapb.payment_bank_value, ctpw.com_task_payment_way_name, hcas.com_account_state_value, hft.end_month, hft.end_type, hca.ukey_store, hcau.ukstore_value, hcat.type_value, hctet.end_type_value, DATE_FORMAT(hft.strart_handle_date, '%Y-%m-%d %H:%i') AS strart_handle_date_string, DATE_FORMAT(hft.send_check_date, '%Y-%m-%d %H:%i') AS send_check_date_string, DATE_FORMAT(hft.finish_date, '%Y-%m-%d %H:%i') AS finish_date_string, hca.remark as comAccountRemark, hft.remark as taskRemark FROM hf_com_task hft LEFT JOIN hf_com_account hca ON hft.com_account_id = hca.com_account_id LEFT JOIN hf_com_account_class hcac ON hft.com_account_class_id = hcac.com_account_class_id LEFT JOIN hf_com_account_state hcas ON hca.state = hcas.com_account_state_code LEFT JOIN hf_com_account_payment_bank hcapb ON hca.payment_bank = hcapb.payment_bank_code LEFT JOIN hf_com_account_ukstore hcau ON hca.ukey_store = hcau.ukstore_code LEFT JOIN hf_com_account_type hcat ON hca.hf_account_type = hcat.type_code LEFT JOIN sal_company cc ON hft.company_id = cc.company_id LEFT JOIN hf_com_task_category ctc ON hft.task_category = ctc.task_category_code LEFT JOIN hf_type ht ON hft.hf_type = ht.hf_type_code LEFT JOIN hf_com_task_payment_way ctpw ON hft.payment_way = ctpw.com_task_payment_way_code LEFT JOIN hf_com_task_end_type hctet ON hft.end_type = hctet.end_type_code
AND hft.is_active = 1
AND cc.title LIKE CONCAT('%', #{companyName}, '%')
AND hft.task_category = #{taskCategory}
AND hft.company_id = #{companyId}
AND ht.hf_type_name = TRIM(#{hfTypeName})
AND hft.hf_type = #{hfType}
AND hft.task_status IN (${taskStatusString})
AND hft.task_status =${taskStatus}
AND hca.hf_account_type = ${hfAccountType}
AND hca.payment_bank = ${paymentBank}
ORDER BY hft.com_task_id
