mapper.select:
SELECT hft.com_task_id, hca.com_account_id, hcac.com_account_class_id, hft.hf_com_account, hft.com_start_month, hcac.operate_start_month, hft.task_category, ctc.task_category_name, ht.hf_type_name, hft.hf_type, hft.end_month, hft.company_id, hft.leader_ship_name, cc.title as company_name, hft.handle_user_id, hft.handle_user_name, hft.submitter_id, hft.submitter_name, DATE_FORMAT(hft.submit_time, '%Y-%m-%d %H:%i') AS submit_time_string, hft.submit_remark, hft.com_account_name, hft.close_day, hft.payment_way, ctpw.com_task_payment_way_name, hft.task_status, hcas.com_account_state_value, hcapb.payment_bank_value, hca.ukey_store, hcau.ukstore_value, hcat.type_value, hctet.end_type_value, hft.strart_handle_date, hft.send_check_date, hft.finish_date , hca.remark as comAccountRemark FROM hf_com_task hft LEFT JOIN hf_com_account hca ON hft.com_account_id = hca.com_account_id LEFT JOIN hf_com_account_class hcac ON hft.com_account_class_id = hcac.com_account_class_id LEFT JOIN hf_com_account_state hcas ON hca.state = hcas.com_account_state_code LEFT JOIN hf_com_account_payment_bank hcapb ON hca.payment_bank = hcapb.payment_bank_code LEFT JOIN hf_com_account_ukstore hcau ON hca.ukey_store = hcau.ukstore_code LEFT JOIN hf_com_account_type hcat ON hca.hf_account_type = hcat.type_code LEFT JOIN sal_company cc ON hft.company_id = cc.company_id LEFT JOIN sal_af_company comAf ON hft.company_id = comAf.company_id LEFT JOIN hf_com_task_category ctc ON hft.task_category = ctc.task_category_code LEFT JOIN hf_type ht ON hft.hf_type = ht.hf_type_code LEFT JOIN hf_com_task_payment_way ctpw ON hft.payment_way = ctpw.com_task_payment_way_code LEFT JOIN hf_com_task_end_type hctet ON hft.end_type = hctet.end_type_code
AND hft.is_active = 1
AND cc.title LIKE CONCAT('%', #{companyName}, '%')
AND hft.task_category = #{taskCategory}
AND hft.hf_com_account LIKE CONCAT('%', #{hfComAccount}, '%')
AND hft.company_id = #{companyId}
AND ht.hf_type_name = TRIM(#{hfTypeName})
AND hft.hf_type = #{hfType}
AND hft.task_status IN (${taskStatusString})
AND hft.task_status =${taskStatus}
AND hca.hf_account_type = ${hfAccountType}
AND hca.payment_bank = ${paymentBank}
AND hft.leader_ship_name LIKE CONCAT('%', #{leaderShipName}, '%')
AND comAf.service_center_id = #{serviceCenterValue}
ORDER BY hft.com_task_id

SELECT hft.com_task_id, hca.com_account_id, hcac.com_account_class_id, hcac.hf_com_account, hcac.com_start_month, hcac.operate_start_month, hft.task_category, ctc.task_category_name, ht.hf_type_name, hft.hf_type, hft.company_id, hft.leader_ship_name, hft.task_status, cc.title as company_name, hft.handle_user_id, hft.handle_user_name, hft.submitter_id, hft.submitter_name, DATE_FORMAT(hft.submit_time, '%Y-%m-%d %H:%i') AS submit_time_string, hft.submit_remark, hca.com_account_name, hca.close_day, hca.payment_way, hca.payment_bank, hcapb.payment_bank_value, ctpw.com_task_payment_way_name, hcas.com_account_state_value, hft.end_month, hft.end_type, hft.com_account_name as com_account_name_task , hft.payment_way as payment_way_task, hca.ukey_store, hcau.ukstore_value, hcat.type_value, hctet.end_type_value, hft.strart_handle_date, hft.send_check_date, hft.finish_date, hca.remark as comAccountRemark, hft.remark as taskRemark FROM hf_com_task hft LEFT JOIN hf_com_account hca ON hft.com_account_id = hca.com_account_id LEFT JOIN hf_com_account_class hcac ON hft.com_account_class_id = hcac.com_account_class_id LEFT JOIN hf_com_account_state hcas ON hca.state = hcas.com_account_state_code LEFT JOIN hf_com_account_payment_bank hcapb ON hca.payment_bank = hcapb.payment_bank_code LEFT JOIN hf_com_account_ukstore hcau ON hca.ukey_store = hcau.ukstore_code LEFT JOIN hf_com_account_type hcat ON hca.hf_account_type = hcat.type_code LEFT JOIN sal_company cc ON hft.company_id = cc.company_id LEFT JOIN sal_af_company comAf ON hft.company_id = comAf.company_id LEFT JOIN hf_com_task_category ctc ON hft.task_category = ctc.task_category_code LEFT JOIN hf_type ht ON hft.hf_type = ht.hf_type_code LEFT JOIN hf_com_task_payment_way ctpw ON hft.payment_way = ctpw.com_task_payment_way_code LEFT JOIN hf_com_task_end_type hctet ON hft.end_type = hctet.end_type_code
AND hft.is_active = 1
AND cc.title LIKE CONCAT('%', #{companyName}, '%')
AND hft.task_category = #{taskCategory}
AND hft.company_id = #{companyId}
AND ht.hf_type_name = TRIM(#{hfTypeName})
AND hft.hf_type = #{hfType}
AND hft.task_status IN (${taskStatusString})
AND hft.task_status =${taskStatus}
AND hca.hf_account_type = ${hfAccountType}
AND hca.payment_bank = ${paymentBank}
AND hft.leader_ship_name LIKE CONCAT('%', #{leaderShipName}, '%')
AND comAf.service_center_id = #{serviceCenterValue}
AND hcac.hf_com_account LIKE CONCAT('%', #{hfComAccount}, '%')
ORDER BY hft.com_task_id

SELECT
    COUNT(*)
FROM
    hf_com_task
WHERE
    is_active = 1
AND task_status IN (0, 1, 2)
AND company_id = #{companyId}
AND hf_type = #{hfType}
AND task_category = #{taskCategory}
