mapper.update:
UPDATE hf_month_charge
SET

    is_active = 0,


    payment_type = #{chgPaymentType},

modified_time = NOW(),
modified_by = #{modifiedBy}

    is_active = 1

        AND emp_archive_id = #{empArchiveId}


        AND company_id = #{companyId}


        AND employee_id = #{employeeId}



            #{item}



        AND emp_task_id != #{exceptEmpTaskId}


        AND hf_type = #{hfType}


        AND hf_month = #{hfMonth}


        AND ss_month_belong >= #{ssMonthBelongStart}


        AND #{ssMonthBelongEnd} >= ss_month_belong


        AND payment_type in (${paymentTypes})

mapper.select:
SELECT
    ss_month_belong,
    IFNULL(SUM(amount),0) AS amount,
    IFNULL(SUM(emp_amount),0) AS emp_amount,
    IFNULL(SUM(com_amount),0) AS com_amount
FROM hf_month_charge

    is_active = 1
    AND payment_type = 11

        AND company_id = #{companyId}


        AND employee_id = #{employeeId}


        AND emp_archive_id = #{empArchiveId}


        AND hf_type = #{hfType}


        AND #{hfMonth} > hf_month


        AND ss_month_belong >= #{ssMonthBelongStart}


        AND #{ssMonthBelongEnd} >= ss_month_belong


GROUP BY ss_month_belong

SELECT hmc.hf_type, hmc.employee_id, ee.employee_name, CASE WHEN hmc.hf_type = 1 THEN heab.hf_emp_account ELSE heaa.hf_emp_account END hf_emp_account, heaab.hf_emp_account AS basic_hf_emp_account, ee.id_num, hmc.hf_month, hmc.ss_month_belong, hmc.payment_type, hmc.repair_reason, hmc.base, hmc.ratio, hmc.amount, hmc.company_id, sc.title AS company_name, CASE WHEN hmc.hf_type = 1 THEN hcacb.hf_com_account ELSE hcaca.hf_com_account END hf_com_account FROM hf_month_charge hmc INNER JOIN sal_company sc ON hmc.company_id = sc.company_id AND sc.is_active = 1
INNER JOIN emp_employee ee ON hmc.employee_id = ee.employee_id LEFT JOIN hf_emp_archive heab ON hmc.emp_archive_id = heab.emp_archive_id AND heab.hf_type = 1 AND heab.is_active = 1 LEFT JOIN hf_com_account hcab ON heab.com_account_id = hcab.com_account_id AND hcab.is_active = 1 LEFT JOIN hf_com_account_class hcacb ON heab.com_account_id = hcacb.com_account_id AND hcacb.hf_type = 1 AND hcab.is_active = 1 LEFT JOIN hf_emp_archive heaa ON hmc.emp_archive_id = heaa.emp_archive_id AND heaa.hf_type = 2 AND heaa.is_active = 1 LEFT JOIN hf_emp_archive heaab ON heaab.emp_archive_id = heaa.belong_emp_archive_id AND heaab.hf_type = 1 AND heaab.is_active = 1 LEFT JOIN hf_com_account hcaa ON heaa.com_account_id = hcaa.com_account_id AND hcaa.is_active = 1 LEFT JOIN hf_com_account_class hcaca ON heaa.com_account_id = hcaca.com_account_id AND hcaca.hf_type = 2 AND hcaa.is_active = 1
hmc.is_active = 1
AND hmc.employee_id = #{employeeId}
AND ee.employee_name LIKE CONCAT('%', #{employeeName}, '%')
AND hmc.company_id = #{companyId}
AND sc.title LIKE CONCAT('%', #{companyName}, '%')
AND hmc.hf_month = #{hfMonth}
AND heab.hf_emp_account = #{basicHfEmpAccount}
AND (hcab.hf_account_type = #{hfAccountType} OR hcaa.hf_account_type = #{hfAccountType})
AND hcacb.hf_com_account = #{basicHfComAccount}
AND heaa.hf_emp_account = #{addedHfEmpAccount}
AND hcaca.hf_com_account = #{addedHfComAccount}
AND hmc.hf_type = #{hfType}
AND hmc.payment_type IN (${paymentTypes})
AND hmc.repair_reason != #{exceptRepairReason}
#{item}
#{item}

SELECT hmc.ss_month_belong AS start_month, hmc.company_id, hmc.employee_id, ee.employee_name, ee.birthday, ee.id_num, ee.gender, hea.hf_emp_account, hmc.hf_type, hmc.amount, hmc.base, hmc.com_amount, hmc.emp_amount, hmc.ratio, hmc.ratio_com, hmc.ratio_emp FROM hf_month_charge hmc INNER JOIN hf_emp_archive hea ON hmc.emp_archive_id = hea.emp_archive_id AND hea.is_active = 1 INNER JOIN emp_employee ee ON hea.employee_id = ee.employee_id WHERE hmc.is_active = 1
AND hea.com_account_class_id = #{comAccountClassId}
AND hmc.hf_month = #{hfMonth}
AND hea.hf_type = #{hfType}
AND hmc.payment_type IN (${paymentTypes})
AND hmc.repair_reason != #{exceptRepairReason}

SELECT COUNT(hpa.hf_type) fCount, SUM(hpc.remitted_amount) AS remitted_amount, SUM(hpc.repair_amount) AS repair_amount, SUM(hpc.remitted_count_emp) AS remitted_count_emp, hpa.payment_month, hpc.company_id, hpa.hf_type, ht.hf_type_name, hcapb.payment_bank_value, sc.title, hpa.com_account_id FROM hf_payment_account hpa INNER JOIN hf_payment_com hpc ON hpa.payment_id = hpc.payment_id INNER JOIN hf_com_account hca ON hca.com_account_id = hpc.com_account_id INNER JOIN hf_type ht ON ht.hf_type_code = hpa.hf_type INNER JOIN hf_com_account_payment_bank hcapb ON hcapb.payment_bank_code = hpc.payment_bank INNER JOIN sal_company sc ON hpc.company_id = sc.company_id WHERE hpa.is_active = 1 AND hpc.is_active = 1
AND hpa.payment_id = #{paymentId}
AND hpa.hf_type = hpc.hf_type AND hpa.com_account_id = hpc.com_account_id GROUP BY hpa.com_account_id, hpa.hf_type

SELECT
MIN(hmc.ss_month_belong) AS ss_month_belong_start, MIN(hmc.hf_month) AS hf_month
FROM hf_month_charge hmc

    hmc.is_active = 1
    AND hmc.payment_type IN (2, 3, 4, 5, 6)

        AND hmc.hf_type = #{hfType}


        AND hmc.company_id = #{companyId}


        AND hmc.employee_id = #{employeeId}

SELECT
    hmc.ss_month_belong AS ss_month_belong_end,
    hmc.hf_month AS hf_stop_month
FROM hf_month_charge hmc

    hmc.is_active = 1
    AND hmc.payment_type IN (7, 8)

        AND hmc.hf_type = #{hfType}


        AND hmc.company_id = #{companyId}


        AND hmc.employee_id = #{employeeId}


ORDER BY hmc.created_time DESC LIMIT 1

SELECT hmc.ss_month_belong AS ss_month_belong_start, hmc.hf_month, hmco.ss_month_belong AS ss_month_belong_end, hmco.hf_month AS hf_stop_month FROM hf_month_charge hmc INNER JOIN hf_month_charge hmco ON hmc.company_id = hmco.company_id AND hmc.employee_id = hmco.employee_id AND hmc.hf_type = hmco.hf_type AND hmc.ss_month_belong = PERIOD_ADD(hmco.ss_month_belong, 1) AND hmco.payment_type IN (7, 8)
hmc.is_active = 0 AND hmco.is_active = 0 AND hmc.payment_type IN (2, 3, 4)
AND hmc.hf_type = #{hfType}
AND hmc.company_id = #{companyId}
AND hmc.employee_id = #{employeeId}

SELECT pa.com_account_id,pa.com_account_class_id,pa.hf_type,ca.com_account_name,ca.payment_bank, ca.hf_account_type,pa.hf_com_account,pa.payment_month,lastMonth.remitted_amount_last,lastMonth.remitted_count_emp_last, (select acr.company_id from hf_account_com_relation acr where acr.com_account_id=pa.com_account_id and acr.major_com=1 ORDER BY CREATEd_time desc LIMIT 1)company_id, (SELECT SUM(ifnull(amount, 0)) FROM hf_month_charge mc INNER JOIN hf_emp_archive ea ON ea.emp_archive_id = mc.emp_archive_id WHERE mc.is_active = 1 AND ea.is_active = 1 AND mc.hf_month = pa.payment_month AND mc.hf_type = pa.hf_type AND ea.com_account_class_id = pa.com_account_class_id AND mc.payment_type = 1 ) AS amount_standard, (SELECT SUM(ifnull(amount, 0)) FROM hf_month_charge mc INNER JOIN hf_emp_archive ea ON ea.emp_archive_id = mc.emp_archive_id WHERE mc.is_active = 1 AND ea.is_active = 1 AND mc.hf_month = pa.payment_month AND mc.hf_type = pa.hf_type AND ea.com_account_class_id = pa.com_account_class_id AND mc.payment_type IN (2, 3, 4, 5) ) AS remitted_amount_add, (SELECT SUM(ifnull(amount, 0)) FROM hf_month_charge mc INNER JOIN hf_emp_archive ea ON ea.emp_archive_id = mc.emp_archive_id WHERE mc.is_active = 1 AND ea.is_active = 1 AND mc.hf_month = pa.payment_month AND mc.hf_type = pa.hf_type AND ea.com_account_class_id = pa.com_account_class_id AND mc.payment_type IN (7, 8, 9, 10) ) AS remitted_amount_reduce, (SELECT SUM(ifnull(amount, 0)) FROM hf_month_charge mc INNER JOIN hf_emp_archive ea ON ea.emp_archive_id = mc.emp_archive_id WHERE mc.is_active = 1 AND ea.is_active = 1 AND mc.hf_month = pa.payment_month AND mc.hf_type = pa.hf_type AND ea.com_account_class_id = pa.com_account_class_id AND mc.payment_type IN (6, 11) ) AS repair_amount, (SELECT count(DISTINCT mc.employee_id) FROM hf_month_charge mc INNER JOIN hf_emp_archive ea ON ea.emp_archive_id = mc.emp_archive_id WHERE mc.is_active = 1 AND ea.is_active = 1 AND mc.hf_month = pa.payment_month AND mc.hf_type = pa.hf_type AND ea.com_account_class_id = pa.com_account_class_id AND mc.payment_type IN (6, 11) ) AS repair_count_emp, (SELECT count(DISTINCT mc.employee_id) FROM hf_month_charge mc INNER JOIN hf_emp_archive ea ON ea.emp_archive_id = mc.emp_archive_id WHERE mc.is_active = 1 AND ea.is_active = 1 AND mc.hf_month = pa.payment_month AND mc.hf_type = pa.hf_type AND ea.com_account_class_id = pa.com_account_class_id AND mc.payment_type = 1 ) AS remitted_emp_count_standard, (SELECT count(DISTINCT mc.employee_id) FROM hf_month_charge mc INNER JOIN hf_emp_archive ea ON ea.emp_archive_id = mc.emp_archive_id WHERE mc.is_active = 1 AND ea.is_active = 1 AND mc.hf_month = pa.payment_month AND mc.hf_type = pa.hf_type AND ea.com_account_class_id = pa.com_account_class_id AND mc.payment_type IN ( 2, 3, 4, 5) ) AS remitted_count_emp_add, (SELECT count(DISTINCT mc.employee_id) FROM hf_month_charge mc INNER JOIN hf_emp_archive ea ON ea.emp_archive_id = mc.emp_archive_id WHERE mc.is_active = 1 AND ea.is_active = 1 AND mc.hf_month = pa.payment_month AND mc.hf_type = pa.hf_type AND ea.com_account_class_id = pa.com_account_class_id AND mc.payment_type IN (7, 8, 9, 10) ) AS remitted_count_emp_reduce, (SELECT remitted_emp_count_standard + remitted_count_emp_add ) AS remitted_count_emp, (SELECT ifnull(amount_standard,0) + ifnull(remitted_amount_add,0) ) AS remitted_amount FROM (SELECT #{hfMonth} AS payment_month,cac.* from hf_com_account_class cac WHERE cac.is_active = 1) pa LEFT JOIN hf_com_account ca ON ca.com_account_id = pa.com_account_id LEFT JOIN ( SELECT pc.com_account_id, pc.hf_type, sum(remitted_amount) remitted_amount_last, sum(remitted_count_emp) remitted_count_emp_last FROM hf_payment_com pc INNER JOIN hf_payment p ON p.payment_id = pc.payment_id WHERE pc.is_active = 1 AND p.payment_month = period_add( #{hfMonth} ,- 1) GROUP BY pc.com_account_id, pc.hf_type ) lastMonth ON lastMonth.com_account_id = pa.com_account_id AND lastMonth.hf_type = pa.hf_type
pa.is_active = 1
AND pa.com_account_class_id = #{comAccountClassId}
AND EXISTS (SELECT 1 FROM hf_account_com_relation WHERE com_account_id=pa.com_account_id AND company_id=#{companyId})
AND EXISTS (SELECT 1 FROM hf_account_com_relation acr INNER JOIN sal_company sc ON sc.company_id=acr.company_id WHERE com_account_id=pa.com_account_id AND sc.title LIKE CONCAT('%', #{companyName}, '%'))
AND heab.hf_emp_account = #{basicHfEmpAccount}
AND ca.hf_account_type = #{hfAccountType}
AND pa.hf_type = 1 AND pa.hf_com_account = #{basicHfComAccount}
AND pa.hf_type = 2 AND pa.hf_emp_account = #{addedHfEmpAccount}
