mapper.select:
SELECT saace.annual_adjust_company_emp_id, saace.annual_adjust_company_id, saace.employee_id, saace.employee_name, saace.ss_serial, saace.salary, saace.chg_salary, saace.id_num, saace.archive_status, saace.base_amount, saace.ss_account_type, saace.emp_classify, saace.settlement_area, saace.ss_account, saace.ss_username, saace.ss_pwd FROM ss_annual_adjust_company_emp saace
INNER JOIN ss_annual_adjust_company saac ON saace.annual_adjust_company_id = saac.annual_adjust_company_id AND saac.is_active = 1
saace.is_active = 1
AND saace.annual_adjust_company_id = #{annualAdjustCompanyId}
AND saac.company_id = #{companyId}
AND saace.employee_id = #{employeeId}
AND saace.employee_name LIKE CONCAT('%', #{employeeName},'%')
AND saace.id_num = #{idNum}
AND saace.ss_serial = #{ssSerial}
AND base_amount >= #{baseAmountStart}
AND #{baseAmountEnd} >= saace.base_amount
