mapper.select:
SELECT sct.company_id, sct.com_task_id, sct.task_category, sct.customer_id, sct.expire_date, sct.submitter_id, sct.submit_time, sct.submit_remark, sct.rejection_remark, sct.handle_remark, cc.title FROM ss_com_task sct LEFT JOIN sal_company cc ON sct.company_id = cc.company_id
AND sct.is_active = 1 AND sct.task_status = 0
AND cc.title LIKE CONCAT('%', #{companyName}, '%')
AND sct.task_category = #{taskCategory}
AND sct.company_id = #{companyId}
AND sct.submit_time >= #{submitTimeStart}
AND sct.submit_time <= #{submitTimeEnd}

SELECT * FROM( SELECT sct.task_status, sct.company_id, sct.com_task_id, sct.task_category, sct.customer_id, sct.expire_date, sct.submitter_id, sct.submit_time, sct.submit_remark, sct.rejection_remark, cc.title FROM ss_com_task sct LEFT JOIN ss_com_account sca ON sct.com_account_id = sca.com_account_id LEFT JOIN sal_company cc ON sct.company_id = cc.company_id
AND sct.is_active = 1
AND sca.ss_account_type = #{accountType}
AND sca.settlement_area LIKE CONCAT('%',#{regionValue}, '%')
AND sct.task_status = #{taskStatus}
AND cc.title LIKE CONCAT('%', #{companyName}, '%')
AND sct.task_category = #{taskCategory}
AND sct.company_id = #{companyId}
AND sct.submit_time >= #{submitTimeStart}
AND sct.submit_time <= #{submitTimeEnd}
)x
AND x.task_status = 1 OR x.task_status = 2

SELECT sct.company_id, sct.com_task_id, sct.task_category, sct.customer_id, sct.expire_date, sct.submitter_id, sct.submit_time, sct.submit_remark, sct.rejection_remark, cc.title FROM ss_com_task sct LEFT JOIN ss_com_account sca ON sct.com_account_id = sca.com_account_id LEFT JOIN sal_company cc ON sct.company_id = cc.company_id
AND sct.is_active = 1 AND sct.task_status =3
AND sca.ss_account_type = #{accountType}
AND sca.settlement_area LIKE CONCAT('%',#{regionValue}, '%')
AND cc.title LIKE CONCAT('%', #{companyName}, '%')
AND sct.task_category = #{taskCategory}
AND sct.company_id = #{companyId}
AND sct.submit_time >= #{submitTimeStart}
AND sct.submit_time <= #{submitTimeEnd}

SELECT sct.company_id, sct.com_task_id, sct.task_category, sct.customer_id, sct.expire_date, sct.submitter_id, sct.submit_time, sct.submit_remark, sct.rejection_remark, cc.title FROM ss_com_task sct LEFT JOIN sal_company cc ON sct.company_id = cc.company_id
AND sct.is_active = 1 AND sct.task_status = 4
AND cc.title LIKE CONCAT('%', #{companyName}, '%')
AND sct.task_category = #{taskCategory}
AND sct.company_id = #{companyId}
AND sct.submit_time >= #{submitTimeStart}
AND sct.submit_time <= #{submitTimeEnd}

