mapper.select:
SELECT sebd.emp_base_period_id, sebd.emp_archive_id, sebd.ss_type, sebd.ss_type_name, sebd.com_policy_item_id, sebd.emp_policy_item_id, sebd.com_base, sebd.emp_base, sebd.com_ratio, sebd.emp_ratio, sebd.com_amount, sebd.emp_amount, sebd.com_addition_amount, sebd.emp_addition_amount, sebd.comemp_amount FROM ss_emp_base_detail sebd WHERE sebd.is_active = 1
AND sebd.emp_base_period_id = #{empBasePeriodId}
AND sebd.emp_archive_id = #{empArchiveId}
